import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, NgForm, Validators } from '@angular/forms';
import { AdminService } from './admin.service';
import { AdminCreate } from './interfaces';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css'],
})
export class AdminComponent implements OnInit {
  adminForm: FormGroup;
  admins: AdminCreate[] | null | undefined;
  http: any;
  ngOnInit() {
    this.getAdmins();
  }
  constructor(private fb: FormBuilder, private adminService: AdminService) {
    this.adminForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', Validators.required],
      dept: ['', Validators.required],
      address: ['', Validators.required],
      gender: ['', Validators.required],
      dob: ['', Validators.required],
      phone: ['', Validators.required],
      posting: ['', Validators.required],
    });
    this.admins = [];
  }



  getAdmins(): void {
    this.adminService.getAdmins().subscribe((admins: AdminCreate[]) => {
      this.admins = admins;
    });

  }

  onSubmit(): void {
    if (this.adminForm.valid) {
      const admin = this.adminForm.value as AdminCreate;
      this.adminService.createAdmin(admin).subscribe(() => {
        this.adminForm.reset();
        this.getAdmins();
      });
    }
  }
  private adminUrl = 'http://localhost:3000/api';

  createAdmin(admin: AdminCreate): Observable<AdminCreate> {
    return this.http.post(`${this.adminUrl}/admin/create`, admin);
  }



  }


<h1>Admin List</h1>

<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Email</th>
      <th>Department</th>
      <th>Address</th>
      <th>Gender</th>
      <th>Date of Birth</th>
      <th>Phone</th>
      <th>Posting</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let admin of admins">
      <td>{{ admin.name }}</td>
      <td>{{ admin.email }}</td>
      <td>{{ admin.dept }}</td>
      <td>{{ admin.address }}</td>
      <td>{{ admin.gender }}</td>
      <td>{{ admin.dob }}</td>
      <td>{{ admin.phone }}</td>
      <td>{{ admin.posting }}</td>
    </tr>
  </tbody>
</table>

<hr>

<h2>Create Admin</h2>

<form [formGroup]="adminForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="name">Name:</label>
    <input type="text" id="name" formControlName="name">
  </div>
  <div>
    <label for="email">Email:</label>
    <input type="email" id="email" formControlName="email">
  </div>
  <div>
    <label for="dept">Department:</label>
    <input type="text" id="dept" formControlName="dept">
  </div>
  <div>
    <label for="address">Address:</label>
    <input type="text" id="address" formControlName="address">
  </div>
  <div>
    <label for="gender">Gender:</label>
    <select id="gender" formControlName="gender">
      <option value="Male">Male</option>
      <option value="Female">Female</option>
    </select>
  </div>
  <div>
    <label for="dob">Date of Birth:</label>
    <input type="date" id="dob" formControlName="dob">
  </div>
  <div>
    <label for="phone">Phone:</label>
    <input type="tel" id="phone" formControlName="phone">
  </div>
  <div>
    <label for="posting">Posting:</label>
    <input type="text" id="posting" formControlName="posting">
  </div>
  <button type="submit">Create Admin</button>
</form>
