import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { NgForm } from '@angular/forms';
import { AuthService } from './auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private http: HttpClient, private authService: AuthService) {}

  submitForm(form: NgForm) {
    const username = form.value['username'];
    const password = form.value['password'];
    const loginType = form.value['login-type'];

    // Perform login validation here based on the login type
    if (loginType === 'employee') {
      // Employee login validation
      this.http.post('http://127.0.0.1:3000/employee/', { username, password }).subscribe((response) => {
        localStorage.setItem('token', response.token);
        console.log('Employee login successful');
        // Redirect to the create admin page
      }, (error) => {
        console.error('Employee login failed', error);
      });
    } else if (loginType === 'manager') {
      // Manager login validation
      this.http.post('http://127.0.0.1:9000/manager/', { username, password }).subscribe((response) => {
        localStorage.setItem('token', response.token);
        console.log('Manager login successful');
        // Redirect to the create admin page
      }, (error) => {
        console.error('Manager login failed', error);
      });
    } else if (loginType === 'admin') {
      // Admin login validation
      this.http.post('http://127.0.0.1:5000/admin/', { username, password }).subscribe((response) => {
        localStorage.setItem('token', response.token);
        console.log('Admin login successful');
        // Redirect to the create admin page
      }, (error) => {
        console.error('Admin login failed', error);
      });
    }
  }
}
