import { Component, OnInit } from '@angular/core';
import { AdminService } from './admin.service';
import { Admin } from './interfaces';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  admins: Admin[]=[];

  constructor(private adminService: AdminService) { }

  ngOnInit() {
    this.getAdmins();
  }

  getAdmins() {
    this.adminService.getAdmins().subscribe(admins => {
      this.admins = admins;
    });
  }

  createAdmin(admin: Admin) {
    this.adminService.createAdmin(admin).subscribe(createdAdmin => {
      this.admins.push(createdAdmin);
    });
  }

  updateAdmin(admin: Admin) {
    this.adminService.updateAdmin(admin.id, admin).subscribe(updatedAdmin => {
      const index = this.admins.findIndex(a => a.id === updatedAdmin.id);
      this.admins[index] = updatedAdmin;
    });
  }

  deleteAdmin(admin: Admin) {
    this.adminService.deleteAdmin(admin.id).subscribe(() => {
      const index = this.admins.findIndex(a => a.id === admin.id);
      this.admins.splice(index, 1);
    });
  }
}
