import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private http: HttpClient) {}

  submitForm() {
    const username = (document.getElementById('username') as HTMLInputElement).value;
    const password = (document.getElementById('password') as HTMLInputElement).value;
    const loginType = (document.getElementById('login-type') as HTMLSelectElement).value;

    // Perform login validation here based on the login type
    if (loginType === 'employee') {
      // Employee login validation
      this.http.post('http://127.0.0.1:3000/employee/', { username, password }).subscribe((response) => {
        console.log('Employee login successful');
      }, (error) => {
        console.error('Employee login failed', error);
      });
    } else if (loginType === 'manager') {
      // Manager login validation
      this.http.post('http://127.0.0.1:9000/manager', { username, password }).subscribe((response) => {
        console.log('Manager login successful');
      }, (error) => {
        console.error('Manager login failed', error);
      });
    } else if (loginType === 'admin') {
      // Admin login validation
      this.http.post('http://127.0.0.1:5000/admin', { username, password }).subscribe((response) => {
        console.log('Admin login successful');
      }, (error) => {
        console.error('Admin login failed', error);
      });
    }
  }
}
<h2>Login</h2>
<form>
  <label for="username">Username:</label>
  <input type="text" id="username">
  <br>
  <label for="password">Password:</label>
  <input type="password" id="password">
  <br>
  <label for="login-type">Login as:</label>
  <select id="login-type">
    <option value="employee">Employee</option>
    <option value="manager">Manager</option>
    <option value="admin">Admin</option>
  </select>
  <br>
  <button type="submit">Login</button>
</form>
