import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { AdminCreate } from './interfaces';


@Injectable({
  providedIn: 'root'
})
export class AdminService {
  private apiUrl = 'http://localhost:5000/';

  constructor(private http: HttpClient) { }

  getAdmins(): Observable<AdminCreate[]> {
    return this.http.get<AdminCreate[]>(`${this.apiUrl}/admins`);
  }

  createAdmin(admin: Admin): Observable<Admin> {
    return this.http.post<Admin>(`${this.apiUrl}/admin`, admin);
  }

  updateAdmin(id: number, admin: Admin): Observable<Admin> {
    return this.http.put<Admin>(`${this.apiUrl}/admin/${id}`, admin);
  }

  deleteAdmin(id: number): Observable<Admin> {
    return this.http.delete<Admin>(`${this.apiUrl}/admin/${id}`);
  }
}
