import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AdminService } from './services/admin.service';
import { Admin } from '../models/admin';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {

  adminForm: FormGroup;
  admins: Admin[];

  constructor(private formBuilder: FormBuilder, private adminService: AdminService) {
    this.adminForm = this.formBuilder.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]],
      dept: ['', Validators.required],
      address: ['', Validators.required],
      gender: ['', Validators.required],
      dob: ['', Validators.required],
      phone: ['', Validators.required],
      posting: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.getAdmins();
  }

  getAdmins(): void {
    this.adminService.getAdmins().subscribe(admins => this.admins = admins);
  }

  onSubmit(): void {
    if (this.adminForm.valid) {
      const admin = this.adminForm.value as Admin;
      this.adminService.createAdmin(admin).subscribe(() => {
        this.adminForm.reset();
        this.getAdmins();
      });
    }
  }

  createAdmin(admin: Admin) {
    this.adminService.createAdmin(admin).subscribe(createdAdmin => {
      this.admins.push(createdAdmin);
    });
  }
}
