import { Component } from '@angular/core';
import { Employee } from '../models/employee';
import { Manager } from '../models/manager';
import { Admin } from '../services/admin.service';

@Component({
  selector: 'app-employee-registration',
  templateUrl: './employee-registration.component.html',
  styleUrls: ['./employee-registration.component.css']
})
export class EmployeeRegistrationComponent {
  employee: Employee = new Employee();
  managers: Manager[] = [];

  constructor(private adminService: AdminService) {
    // Fetch the list of managers from the AdminService
    this.adminService.getManagers().subscribe(managers => this.managers = managers);
  }

  addEmployee() {
    // Call the AdminService to add the employee and assign the manager
    this.adminService.addEmployee(this.employee.name, this.employee.email, this.employee.password, this.employee.managerId).subscribe(
      () => {
        console.log('Employee added successfully');
        // Reset the form fields
        this.employee = new Employee();
      },
      error => {
        console.error('Failed to add employee', error);
        // Display an error message to the user
      }
    );
  }
}
