import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AdminService } from './admin.service';
import { AdminCreate } from './interfaces';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css'],
})
export class AdminComponent implements OnInit {
  adminForm: FormGroup;
  admins: AdminCreate[] = [];

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private adminService: AdminService
  ) 
  ngOnInit(){
    this.adminForm = this.formBuilder.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(8)]],
      dept: ['', Validators.required],
      address: ['', Validators.required],
      gender: ['', Validators.required],
      dob: ['', Validators.required],
      phone: ['', Validators.required],
      posting: ['', Validators.required],
    });
  }

  ngOnInit(): void {
    this.getAdmins();
  }

  getAdmins(): void {
    this.adminService.getAdmins().subscribe((admins: AdminCreate[]) => {
      this.admins = admins;
    });
  }

  onSubmit(): void {
    if (this.adminForm.valid) {
      const admin = this.adminForm.value as AdminCreate;
      this.adminService.createAdmin(admin).subscribe(() => {
        this.adminForm.reset();
        this.getAdmins();
      });
    }
  }
}
<h1>Admin List</h1>
<table>
    <thead>
      <tr>
        <th>Name</th>
        <th>Email</th>
        <th>Department</th>
        <th>Address</th>
        <th>Gender</th>
        <th>Date of Birth</th>
        <th>Phone</th>
        <th>Posting</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let admin of admins">
        <td>{{ admin.name }}</td>
        <td>{{ admin.email }}</td>
        <td>{{ admin.dept }}</td>
        <td>{{ admin.address }}</td>
        <td>{{ admin.gender }}</td>
        <td>{{ admin.dob }}</td>
        <td>{{ admin.phone }}</td>
        <td>{{ admin.posting }}</td>
      </tr>
    </tbody>
</table>
